pragma solidity ^0.4.24;

import "../node_modules/zeppelin-solidity/contracts/lifecycle/Pausable.sol";

import "../node_modules/zeppelin-solidity/contracts/ownership/Ownable.sol";
import "../node_modules/zeppelin-solidity/contracts/math/SafeMath.sol";
import "../node_modules/zeppelin-solidity/contracts/ECRecovery.sol";


contract ETHDenverStaking is Ownable, Pausable {

    using SafeMath for uint256;
    using ECRecovery for bytes32;

    event UserStake(address userAddress, address stakedBy, uint amountStaked);
    event debug(bytes32 _msg);
    event debugAddress(address _msg);

    address authorizedStakeGrantAddress = 0xc1E19BDD3DBBA6c070579083E3B0D4C92fcaE0B3;

    mapping (address => uint) stakedBalances;

    function testEvent(address _userAddress, address _stakedBy, uint _amountStaked) public {
        emit UserStake(_userAddress, _stakedBy, _amountStaked);
    }


    function stake(address _userAddress, uint _expiringDate, bytes _signature) public payable {
        bytes32 hashMessage = keccak256(abi.encodePacked(_userAddress));
        emit debug(hashMessage);
        address signer = hashMessage.toEthSignedMessageHash().recover(_signature);
        emit debugAddress(signer);

        // require(signer == authorizedStakeGrantAddress, "Signer is not autorized");
    }

}
